///saga effect
import { put, takeLatest } from "redux-saga/effects";
import { Api } from "./Api";

function* fetchProduct() {
  // yield will wait for Promise to resolve
  const response = yield fetch("http://localhost:8000/product");
  // Again yield will wait for Promise to resolve
  const data = yield response.json();
  console.log(data);
  yield put({ type: "fETCH_SUCCESSED", payload: data });
}

export function* watchFetchProduct() {
  yield takeLatest("FETCH_PRODUCT", fetchProduct);
}
