import axios from "axios";
const url = "http://localhost:8000/product/";

function* getProductFromApi() {
  const response = axios(url);
  const product = yield response.status === 200 ? JSON.parse(response._bodyInit) : [];
  console.log(product);
}

export const Api = {
  getProductFromApi,
};
